sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var whatNeededField = g_form.getControl("u_what_needed");

    if (isLoading && !g_form.isNewRecord()) {
        whatNeededField.value = g_form.getValue("u_what_needed");
        return;
    }

    var options = {
        "hr": [{
                name: "Human Resources 1",
                value: "hr1"
            },
            {
                name: "Human Resources 2",
                value: "hr2"
            }
        ],
        "facilities": [{
                name: "Facilities 1",
                value: "facilities1"
            },
            {
                name: "Facilities 2",
                value: "facilities2"
            }
        ],
        "legal": [{
                name: "Legal 1",
                value: "legal1"
            },
            {
                name: "Legal 2",
                value: "legal2"
            }
        ]
    };

    whatNeededField.options.length = 0;

    if (options.hasOwnProperty(newValue)) {
        var currentOptions = options[newValue];
        for (var i = 0; i < currentOptions.length; i++) {
            var option = currentOptions[i];
            whatNeededField.options[i] = new Option(option.name, option.value);
        }
    }

    whatNeededField.options[whatNeededField.options.length] = new Option("Other", "other");
}
